Scripts.OnClick:
								self:Disable();
								self:GetFontString():SetTextColor(NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b);
								self.selectedGlow:Show();
								local dungeonsTab = self:GetParent().dungeonsTab;
								dungeonsTab:Enable();
								dungeonsTab:GetFontString():SetTextColor(HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
								dungeonsTab.selectedGlow:Hide();

                                local TechTab = self:GetParent().TechTab;
								TechTab:Enable();
								TechTab:GetFontString():SetTextColor(HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
								TechTab.selectedGlow:Hide();

                                local ServerTab = self:GetParent().ServerTab;
								ServerTab:Enable();
								ServerTab:GetFontString():SetTextColor(HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
								ServerTab.selectedGlow:Hide(); 


								EncounterJournal_TabSwitch(2);
								PlaySound("igMainMenuOptionCheckBoxOn");

								self:Disable();
								self:GetFontString():SetTextColor(NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b);
								self.selectedGlow:Show();
								local raidsTab = self:GetParent().raidsTab;
								raidsTab:Enable();
								raidsTab:GetFontString():SetTextColor(HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
								raidsTab.selectedGlow:Hide();

                                local TechTab = self:GetParent().TechTab;
								TechTab:Enable();
								TechTab:GetFontString():SetTextColor(HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
								TechTab.selectedGlow:Hide();                                

                                local ServerTab = self:GetParent().ServerTab;
								ServerTab:Enable();
								ServerTab:GetFontString():SetTextColor(HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
								ServerTab.selectedGlow:Hide(); 

                                NavBar_Reset(EncounterJournal.navBar)
								EncounterJournal_TabSwitch(1);
								PlaySound("igMainMenuOptionCheckBoxOn");

								self:Disable();
								self:GetFontString():SetTextColor(NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b);
								self.selectedGlow:Show();

                                local raidsTab = self:GetParent().raidsTab;
								raidsTab:Enable();
								raidsTab:GetFontString():SetTextColor(HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
                                raidsTab.selectedGlow:Hide();

                                local dungeonsTab = self:GetParent().dungeonsTab;
								dungeonsTab:Enable();
								dungeonsTab:GetFontString():SetTextColor(HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
								dungeonsTab.selectedGlow:Hide();

                                local ServerTab = self:GetParent().ServerTab;
								ServerTab:Enable();
								ServerTab:GetFontString():SetTextColor(HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
								ServerTab.selectedGlow:Hide(); 


								EncounterJournal_TabSwitch(3);
								PlaySound("igMainMenuOptionCheckBoxOn");

								self:Disable();
								self:GetFontString():SetTextColor(NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b);
								self.selectedGlow:Show();

                                local raidsTab = self:GetParent().raidsTab;
								raidsTab:Enable();
								raidsTab:GetFontString():SetTextColor(HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
                                raidsTab.selectedGlow:Hide();

                                local dungeonsTab = self:GetParent().dungeonsTab;
								dungeonsTab:Enable();
								dungeonsTab:GetFontString():SetTextColor(HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
								dungeonsTab.selectedGlow:Hide();

                                local TechTab = self:GetParent().TechTab;
								TechTab:Enable();
								TechTab:GetFontString():SetTextColor(HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
								TechTab.selectedGlow:Hide();  


								EncounterJournal_TabSwitch(4);
								PlaySound("igMainMenuOptionCheckBoxOn");
